pragma solidity ^0.8.0;

contract Profile {

    struct Driver {
        bool isDriver;          
        uint256 rating;           // Driver rating(score) 
        string carAssetUrl;     // Car image and description ie number of seats available, pointing towards storacha
        string infoAssetUrl;    // Driver image, name , age pointing towards storacha database
        uint256 counterCourse;
        string zkPassport; 
    }

    struct Passenger {
        bool isPassenger;          
        uint256 rating;           // Passenger rating(score) 
        string infoAssetUrl;    // Passenger image, name , age pointing towards storacha database
        uint256 counterCourse;
        string zkPassport; 
    }

    event DriverAdded(address indexed account);
    event DriverRemoved(address indexed account);
    event PassengerAdded(address indexed account); 
    event PassengerRemoved(address indexed account);

    mapping(address => Driver) private drivers;
    mapping(address => Passenger) private passengers;

    // Define modifiers that checks to see if msg.sender has the appropriate role
    modifier onlyDriver() {
        require(isDriver(msg.sender), "Sender isn't a driver");
        _;
    }

    modifier onlyPassenger() {
        require(isPassenger(msg.sender), "Sender isn't a passenger");
        _;
    }

    function addDriver(uint256 _initialRating, string memory _carAssetUrl, string memory _profileAssetUrl) 
        public 
    {
        require(!isDriver(msg.sender), "Sender is already a driver");
        Driver memory driverDetails;
        driverDetails.isDriver = true;
        driverDetails.counterCourse = 0; 
        driverDetails.rating = _initialRating;
        driverDetails.carAssetUrl = _carAssetUrl;
        driverDetails.infoAssetUrl = _profileAssetUrl;
        drivers[msg.sender] = driverDetails;
        emit DriverAdded(msg.sender);
    }

    function addPassenger(uint256 _initialRating, string memory _profileAssetUrl) 
        public 
    {
        require(!isPassenger(msg.sender), "Sender is already a passenger");
        Passenger memory passengerDetail;
        passengerDetail.isPassenger = true;
        passengerDetail.counterCourse = 0; 
        passengerDetail.rating = _initialRating;
        passengerDetail.infoAssetUrl = _profileAssetUrl;
        passengers[msg.sender] = passengerDetail;
        emit PassengerAdded(msg.sender);
    }

    function removePassenger() 
        public
        onlyPassenger
    {
        delete passengers[msg.sender];
        emit PassengerRemoved(msg.sender);
    }

    function removeDriver() 
        public
        onlyDriver
    {
        delete drivers[msg.sender];
        emit DriverRemoved(msg.sender);
    }

    function updateRate(uint256 _newRate, address _account, bool _isDriver) 
        public 
    {
        if (_isDriver) {
            require(isPassenger(msg.sender), "Sender isn't a passenger");
            Driver memory driverDetails = drivers[_account];
            driverDetails.rating = _newRate;
            drivers[_account] = driverDetails; 
        } else {
            require(isDriver(msg.sender), "Sender isn't a driver");
            Passenger memory passengerDetails = passengers[_account];
            passengerDetails.rating = _newRate;
            passengers[_account] = passengerDetails; 
        }
    }

    function isDriver(address account) public view returns (bool) {
        return drivers[account].isDriver;
    }

    function isPassenger(address account) public view returns (bool) {
        return passengers[account].isPassenger;
    }

    function getDriver(address account) public view returns (Driver memory) {
        return drivers[account];
    }

    function getPassenger(address account) public view returns (Passenger memory) {
        return passengers[account];
    }

    function getDriverRate(address account) public view returns (uint256) {
        return drivers[account].rating;
    }

    function getPassengerRate(address account) public view returns (uint256) {
        return passengers[account].rating;
    }

    function getDriverCounter(address account) public view returns (uint256) {
        return drivers[account].counterCourse;
    }

    function getPassengerCounter(address account) public view returns (uint256) {
        return passengers[account].counterCourse;
    }
}","keccak256":"0x0b682f8c3b8008e10387c1fc8d3d82a892e562e5b78074703bc9ace163a38684","license":"MIT"},"contracts/RideManagement.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Profile.sol";

/**
 * @title RideManagement
 * @dev This contract manages rides, including their states and details. It provides functionality for posting, booking, and completing rides.
 */
contract RideManagement {
    Ride[] private _rides;
    mapping(bytes32 => uint256) private _rideIndexMap;
    uint256 private _rideCount = 0;

    /**
     * @dev Enum representing the various states a ride can be in.
     */
    enum RideState {
        Pending,      // Ride has been posted but not yet confirmed by the driver
        Posted,       // Ride has been confirmed by the driver and is available for booking
        Booked,       // Ride has been booked by a passenger
        InProgress,   // Ride is currently in progress
        Completed,    // Ride has been completed
        Canceled,     // Ride has been canceled
        Failed        // Ride has failed or been interrupted
    }

    /**
     * @dev Struct representing a ride.
     */
    struct Ride {
        bytes32 id;
        RideState state;
        address owner;
        address[] passengers;
        uint256 price;
        uint256 timestamp;
        uint256 totalSeats;
        uint256 availableSeats;
        string destination;
    }

    event RidePosted(bytes32 indexed rideId, address indexed owner);
    event RideBooked(bytes32 indexed rideId, address indexed passenger);
    event RideCompleted(bytes32 indexed rideId);
    event RideCanceled(bytes32 indexed rideId);

    modifier onlyOwner(bytes32 rideId) {
        require(_rides[_rideIndexMap[rideId]].owner == msg.sender, "Caller is not the owner");
        _;
    }

    /**
     * @dev Function to post a new ride. Sets the initial state to Pending.
     */
    function postRide(uint256 price, uint256 totalSeats, string memory destination) public {
        bytes32 rideId = keccak256(abi.encodePacked(msg.sender, block.timestamp, _rideCount));
        Ride memory newRide = Ride({
            id: rideId,
            state: RideState.Pending,  // Set initial state to Pending
            owner: msg.sender,
            passengers: new address[](0),
            price: price,
            timestamp: block.timestamp,
            totalSeats: totalSeats,
            availableSeats: totalSeats,
            destination: destination
        });
        _rides.push(newRide);
        _rideIndexMap[rideId] = _rideCount;
        _rideCount++;
        emit RidePosted(rideId, msg.sender);
    }

    /**
     * @dev Function for the driver to confirm a ride. Changes state from Pending to Posted.
     */
    function confirmRide(bytes32 rideId) public onlyOwner(rideId) {
        Ride storage ride = _rides[_rideIndexMap[rideId]];
        require(ride.state == RideState.Pending, "Ride is not in pending state");
        ride.state = RideState.Posted;
        emit RidePosted(ride.id, ride.owner);
    }

    /**
     * @dev Function for a passenger to book a ride. Changes state from Posted to Booked if no available seats remain.
     */
    function bookRide(bytes32 rideId) public {
        Ride storage ride = _rides[_rideIndexMap[rideId]];
        require(ride.state == RideState.Posted, "Ride is not available for booking");
        require(ride.availableSeats > 0, "No available seats");
        ride.passengers.push(msg.sender);
        ride.availableSeats--;
        if (ride.availableSeats == 0) {
            ride.state = RideState.Booked;
        }
        emit RideBooked(rideId, msg.sender);
    }

    /**
     * @dev Function for the driver to mark a ride as completed. Changes state from Booked to Completed.
     */
    function completeRide(bytes32 rideId) public onlyOwner(rideId) {
        Ride storage ride = _rides[_rideIndexMap[rideId]];
        require(ride.state == RideState.Booked, "Ride is not booked");
        ride.state = RideState.Completed;
        emit RideCompleted(rideId);
    }

    /**
     * @dev Function for the driver to cancel a ride. Can be called in Pending, Posted, or Booked states.
     */
    function cancelRide(bytes32 rideId) public onlyOwner(rideId) {
        Ride storage ride = _rides[_rideIndexMap[rideId]];
        require(ride.state == RideState.Pending || ride.state == RideState.Posted || ride.state == RideState.Booked, "Ride cannot be canceled");
        ride.state = RideState.Canceled;
        emit RideCanceled(rideId);
    }

    function getRide(bytes32 rideId) public view returns (Ride memory) {
        return _rides[_rideIndexMap[rideId]];
    }

    function getAllRides() public view returns (Ride[] memory) {
        return _rides;
    }
}","keccak256":"0xccdb27db185c290b03221736f73a2b117f5ccef69a4e4cc0cc35129e87c4ce27","license":"MIT"}},"version":1}
